import React from 'react';
import './Pokedex.css';

const Pokecard = (props) => {
	let imgSrc = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${props.id}.png`;
	return (
		<div className="Pokecard">
			<h2>{props.name}</h2>
			<img src={imgSrc} />
			<p>Type: {props.type}</p>
			<p>EXP: {props.exp}</p>
		</div>
	);
};

export default Pokecard;
